            this.overlay = {
                opened: false,
                collapsed: false,
                normalHeight: null,
                $el: null,
                $slides: null,
                slides: []
            };
            this.$wrapper = null;
            this.$backdrop = null;
            this.activeTab = null;
            this.slides = [];
            this.activeSlide = 0;
