                var scrollContainer = $(selector),
                    containerHeight = scrollContainer[0].clientHeight,
                    contentHeight = scrollContainer[0].scrollHeight,
                    scrollTop = scrollContainer.scrollTop(),
                    padding = (isNaN(offset)) ? 0 : offset;

